protected void kosong(){
    id.setText("");
    id.setEnabled(false);
        namas.setText("");
        kodesp.setText("");
        tanggal.setDate(null);
            kategori.setText("");
            jenis.setText("");
            namab.setText("");
            stok.setText("");
            txtcari.setText("");
    }
 protected void datatable(){
Object[] Baris ={"ID TRANSAKSI","TANGGAL","NAMA SUPPLIER","KODE BARANG","NAMA BARANG","KATEGORI","JENIS","STOK MASUK"};
table = new DefaultTableModel(null, Baris);
String cariitem = txtcari.getText();
try {
String sql = "SELECT * FROM tbl_barang_masuk where id_transaksi like '%"+cariitem+"%' or kategori like '%"+cariitem+"%' or nama_supplier like '%"+cariitem+"%' or kode_barang like '%"+cariitem+"%' or nama_barang like '%"+cariitem+"%' or jenis like '%"+cariitem+"%' order by id_transaksi asc";
java.sql.Statement stat = conn.createStatement();
ResultSet hasil = stat.executeQuery(sql);
while (hasil.next()){
table.addRow(new Object[]{
hasil.getString(1),
hasil.getString(2),
hasil.getString(3),
hasil.getString(4),
hasil.getString(5),
hasil.getString(6),
hasil.getString(7),
hasil.getString(8)
});
}
table_user.setModel(table);
} catch (SQLException e) {
}
 }
 
