    private void saveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_saveActionPerformed

        String sql = "insert into tbl_barang_masuk values (?,?,?,?,?,?,?,?)";
        try{
            PreparedStatement stat = conn.prepareStatement(sql);
            stat.setString(1, id.getText());
            stat.setString(2, tgl);
            stat.setString(3, namas.getText());;
            stat.setString(4, kodesp.getText());
            stat.setString(5, namab.getText());
            stat.setString(6, kategori.getText());
            stat.setString(7, jenis.getText());
            stat.setString(8, stok.getText());
            stat.executeUpdate();
            JOptionPane.showMessageDialog(null, "data berhasil disimpan");
            id.requestFocus();
        }
        catch (SQLException e){
            JOptionPane.showMessageDialog(null, "data gagal disimpan"+e);
        }finally{ 
            try{
            sql = "update tbl_barang set stok=? where kode_barang='"+kodesp.getText()+"'";
            PreparedStatement stat = conn.prepareStatement(sql);
            stat.setString(1, stokupdate.getText());
            stat.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil DiUpdate!");
             kosong();
             nomer();
             stokupdate.requestFocus();
        }
            catch (SQLException e){
        }
                       datatable();
        }
    }//GEN-LAST:event_saveActionPerformed

    private void deleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_deleteActionPerformed
        int ok = JOptionPane.showConfirmDialog(null,"hapus","konfirmasi dialog",JOptionPane.YES_NO_OPTION);
        if (ok==0){
            String sql = "delete from tbl_barang_masuk where id_transaksi ='"+id.getText()+"'";
            try{
                PreparedStatement stat = conn.prepareStatement(sql);
                stat.executeUpdate();
                JOptionPane.showMessageDialog(null, "data berhasil dihapus");
                kosong();
                nomer();
                id.requestFocus();
                datatable();
            }
            catch (SQLException e){
                JOptionPane.showMessageDialog(null, "data gagal dihapus"+e);
            }
        }
    }//GEN-LAST:event_deleteActionPerformed

